import { Component, Input } from '@angular/core';

import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  products = products;

  share(url: string) {
    window.open(`https://t.me/share/url?url=${url}`, "_blank");
  }
}
<h2>Products</h2>

<div *ngFor="let product of products">
  <h3>
    <a href="{{product.url}}" target="_blank" [title]="product.name + ' details'">
      {{ product.name }}
    </a>
  </h3>
  <img *ngIf="product.image" src="{{product.image}}">
  <p *ngIf="product.price"> Price: {{product.price}} ₸.</p>
  <p *ngIf="product.description">Description: {{ product.description }}</p>
  <p *ngIf="product.rating">Rating: {{ product.rating }} out of 5</p>
  <button type="button" (click)="share(product.url)">Share</button>
  <a href={{product.link}} alt="Kaspi"><img src="https://troublemakers.tv/wp-content/uploads/Client_kaspi.png" height="36"></a>
  <p>
  </p>
</div>
